import axios from "axios";

// Use environment variable for production, fallback to localhost for development
const API = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5000/api",
  timeout: 30000, // Increased timeout for production
});

// Request interceptor to attach JWT token
API.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    console.log("üåê Making request to:", config.baseURL + config.url);
    console.log("üîß Environment:", process.env.NODE_ENV);
    console.log("üîß API URL:", process.env.REACT_APP_API_URL || "localhost");
    return config;
  },
  (error) => {
    console.error("‚ùå Request error:", error);
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
API.interceptors.response.use(
  (response) => {
    console.log("‚úÖ API Response received:", response.status);
    return response;
  },
  (error) => {
    console.error("‚ùå API Error:", error);
    console.error("‚ùå Error response:", error.response?.data);
    console.error("‚ùå Error status:", error.response?.status);
    console.error("‚ùå Request URL:", error.config?.url);
    console.error("‚ùå Base URL:", error.config?.baseURL);

    if (error.code === "ECONNABORTED") {
      console.error("‚è∞ Request timeout - Server might be slow or unavailable");
    }
    if (error.code === "ERR_NETWORK") {
      console.error(
        "üåê Network Error: Check your internet connection and server availability"
      );
    }
    return Promise.reject(error);
  }
);

export default API;
